package JavaBasics;

public class LoopsConcepts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Whileloopconcepts
		//printing 1 to 10
		System.out.println("while loop  concepts");
		int i=1;
		while(i<=10) {

			System.out.println(i);
			i=i+1;

		}

		System.out.println("************************************************");
		System.out.println("For loop Concepts");
		//forloop
		//printing 1 to 10

		for (int j=1 ; j<=10;j++) {

			System.out.println(j);

		}


		System.out.println("+++++++++++++++++++++++++++++++++++++++++");

		//while loop
		//print 10 to 1

		int k=10;
		while(k>=0) {

			System.out.println(k);
			k--;
		}
		
		
		System.out.println("forloop reverse print");
		//forloop
		//print 10 to 1
		
		for (int l=10;l>=0;l--) {
			
			System.out.println(l);
			
		}
	}

}
